];

const OptIn = ({ setSubmitted, submitted }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const navigate = useNavigate();
    const location = useLocation();

    const styles = {
      carousel: {
        width: '50%',
        margin: '20px 0',
      },
      testimonialImage: {
        maxHeight: '150px',
        width: 'auto',
      },
      arrow: {
        position: 'absolute',
        zIndex: 2,
        top: 'calc(50% - 15px)',
        width: 30,
        height: 30,
        cursor: 'pointer',
        backgroundColor: 'rgba(0,0,0,0.5)',
        color: '#ffffff',
        fontSize: '2rem',
        borderRadius: '50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      },
      footer: {
        background: '#272122',
        color: '#fff',
        padding: '20px',
        textAlign: 'center',
        marginTop: 'auto',
      }
    };

    useEffect(() => {
        localStorage.removeItem('formFilled');
    }, []);

    useEffect(() => {
        if(location.pathname === '/thankyou' && !submitted) {
            navigate('/');
        }
    }, [location, navigate, submitted]);

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const data = { firstName, lastName, email };

            const response = await fetch('http://localhost:3000/posts', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(data)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            } else {
                const result = await response.json();
                console.log(result);

                setEmail('');
                setFirstName('');
                setLastName('');
                localStorage.setItem('formFilled', 'true');
                navigate('/thankyou');
            }

        } catch (error) {
            console.error('An error occurred:', error);
        }
    };

    return (
        <div style={{display: 'flex', flexDirection: 'column', minHeight: '100vh'}}>
            <div style={{width: '100%', overflow: 'hidden'}}>
                <img src="/banner monica.png" alt="Banner" style={{width: '100%', height: 'auto'}}/>
            </div>
            <div style={{width: '100%', padding: '20px', background: 'transparent', textAlign: 'center'}}>
                <p style={{color: '#272122', fontStyle: 'Nourd Bold', fontSize: '20px'}}> Get to know an amazing lineup of renowned experts in the fields of positive psychology, mindfulness, resilience, happiness, self-care and more who are changing lives, schools, workplaces, and communities... And learn how you can experience more love, cope with stress, navigate change, and feel more joy.</p>
            </div>
            <div style={{width: '100%', overflow: 'hidden', padding: '20px'}}>
                <form onSubmit={handleSubmit} style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
                    <input type="text" placeholder="First Name" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                    <input type="text" placeholder="Last Name" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                    <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <button type="submit">Submit</button>
                </form>
            </div>
            <Carousel style={styles.carousel}>
                {testimonials.map((testimonial, index) => (
                  <div key={index}>
                    <img src={testimonial.photo} style={styles.testimonialImage} alt={testimonial.name} />
                    <p>{testimonial.testimonial}</p>
                  </div>
                ))}
            </Carousel>
            <footer style={styles.footer}>
                <p>© 2023 by Monica Garrett. Proudly created with React.js</p>
            </footer>
        </div>
    );
};

export default OptIn;
