import React, { useEffect } from 'react';


const SquarePaymentForm = () => {

  useEffect(() => {
    if (window.SqPaymentForm) {
      const paymentForm = new window.SqPaymentForm({
        applicationId: 'YOUR_SQUARE_APPLICATION_ID',
        locationId: 'YOUR_SQUARE_LOCATION_ID',
        inputClass: 'sq-input',
        autoBuild: false,
        inputStyles: [{
          fontSize: '16px',
          lineHeight: '24px',
          padding: '16px',
          placeholderColor: '#a0a0a0',
          backgroundColor: 'transparent',
        }],
        cardNumber: {
          elementId: 'sq-card-number',
          placeholder: 'Card Number',
        },
        cvv: {
          elementId: 'sq-cvv',
          placeholder: 'CVV',
        },
        expirationDate: {
          elementId: 'sq-expiration-date',
          placeholder: 'MM/YY',
        },
        postalCode: {
          elementId: 'sq-postal-code',
        },
        callbacks: {
          cardNonceResponseReceived: function(errors, nonce, cardData, billingContact, shippingContact) {
            if (errors) {
              console.error('Encountered errors:');
              errors.forEach(function(error) {
                console.error('  ' + error.message);
              });
              alert('Encountered errors, check console for more details');
              return;
            }
            alert(`The generated nonce is:\n${nonce}`);
            //TODO: Replace alert with the function that captures the nonce for a payment
          },
        },
      });

      paymentForm.build();

      const paymentFormSubmit = document.getElementById('sq-creditcard');
      paymentFormSubmit.addEventListener('click', function(event) {
        event.preventDefault();
        paymentForm.requestCardNonce();
      });
    }
  }, []);

  return (
    <div id="form-container">
      <div id="sq-card-number"></div>
      <div className="third" id="sq-expiration-date"></div>
      <div className="third" id="sq-cvv"></div>
      <div className="third" id="sq-postal-code"></div>
      <button id="sq-creditcard" className="button-credit-card">Pay $1.00</button>
    </div>
  );
};

export default SquarePaymentForm;
