import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // imports the carousel css

// Mock data
const testimonials = [
  {
    name: 'John Doe',
    photo: 'client/public/lady pic.jpeg', // replace with your image link
    testimonial: 'This is a great site!'
  },
  {
    name: 'Jane Smith',
    photo: 'client/public/lady pic.jpeg', // replace with your image link
    testimonial: 'I found what I was looking for!'
  },
  // Add more testimonials as needed
];

const ThankYou = () => {
  const navigate = useNavigate();

  useEffect(() => {
    if (!localStorage.getItem('formFilled')) {
      navigate('/optin');
    }
  }, [navigate]);

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      backgroundColor: '#f8f9fa',
    },
    content: {
      textAlign: 'center',
    },
    title: {
      fontSize: '2.5rem',
      color: '#343a40',
      fontFamily: 'Arial, sans-serif',
    },
    subtitle: {
      fontSize: '1.5rem',
      color: '#6c757d',
      fontFamily: 'Arial, sans-serif',
    },
    carousel: {
      width: '50%',
      margin: '50px 0',
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <h2 style={styles.title}>Thank You!</h2>
        <p style={styles.subtitle}>Your form has been submitted successfully.</p>
        <Carousel style={styles.carousel} showThumbs={false}>
          {testimonials.map((testimonial, index) => (
            <div key={index}>
              <img src={testimonial.photo} alt={testimonial.name} />
              <h3>{testimonial.name}</h3>
              <p>{testimonial.testimonial}</p>
            </div>
          ))}
        </Carousel>
      </div>
    </div>
  );
};

export default ThankYou;
