import React from 'react';
import './SquarePaymentForm.css';

class SquarePaymentForm extends React.Component {
  constructor(props) {
    super(props);
    this.paymentForm = null;
  }

  componentDidMount() {
    if (!window.SqPaymentForm) {
      alert('SqPaymentForm library not loaded!');
      return;
    }

    this.paymentForm = new window.SqPaymentForm({
      // Initialize the payment form elements
      // TODO: Replace with your sandbox application ID
      applicationId: "sandbox-sq0idb-XXXXXXXXXXXXXXXXXXXXXX",
      locationId: "CBASEDm3gRdF8mRHqCHC_w8vqWcgAQ",
      inputClass: 'sq-input',
      autoBuild: false,
      // Customize the CSS for SqPaymentForm iframe elements
      inputStyles: [{
          fontSize: '16px',
          lineHeight: '24px',
          padding: '16px',
          placeholderColor: '#a0a0a0',
          backgroundColor: 'transparent',
      }],
      // Initialize the credit card placeholders
      cardNumber: {
        elementId: 'sq-card-number',
        placeholder: 'Card Number'
      },
      cvv: {
        elementId: 'sq-cvv',
        placeholder: 'CVV'
      },
      expirationDate: {
        elementId: 'sq-expiration-date',
        placeholder: 'MM/YY'
      },
      postalCode: {
        elementId: 'sq-postal-code',
        placeholder: 'Postal'
      },
      // SqPaymentForm callback functions
      callbacks: {
        /*
        * callback function: cardNonceResponseReceived
        * Triggered when: SqPaymentForm completes a card nonce request
        */
        cardNonceResponseReceived: function (errors, nonce, cardData) {
          if (errors) {
            // Log errors from nonce generation to the browser developer console.
            console.error('Encountered errors:');
            errors.forEach(function (error) {
              console.error('  ' + error.message);
            });
            alert('Encountered errors, check browser developer console for more details');
            return;
          }
          alert(`The generated nonce is:\n${nonce}`);
          //TODO: Replace alert with code in step 2.1
        },
        /*
        * callback function: unsupportedBrowserDetected
        * Triggered when: the page loads and an unsupported browser is detected
        */
        unsupportedBrowserDetected: function () {
          /* PROVIDE FEEDBACK TO SITE VISITORS */
        },
        /*
        * callback function: inputEventReceived
        * Triggered when: visitors interact with SqPaymentForm iframe elements.
        */
        inputEventReceived: function (inputEvent) {
          switch (inputEvent.eventType) {
            case 'focusClassAdded':
              /* HANDLE AS DESIRED */
              break;
            case 'focusClassRemoved':
              /* HANDLE AS DESIRED */
              break;
            case 'errorClassAdded':
              /* HANDLE AS DESIRED */
              break;
            case 'errorClassRemoved':
              /* HANDLE AS DESIRED */
              break;
            case 'cardBrandChanged':
              /* HANDLE AS DESIRED */
              break;
            case 'postalCodeChanged':
              /* HANDLE AS DESIRED */
              break;
              default:
                console.log('An unhandled event was received: ' + inputEvent.eventType);
          }
        },
        /*
        * callback function: paymentFormLoaded
        * Triggered when: SqPaymentForm is fully loaded
        */
        paymentFormLoaded: function () {
          /* HANDLE AS DESIRED, EG: ENABLE THE PAYMENT BUTTON */
        }
      }
    });
    this.paymentForm.build();
  }

  componentWillUnmount() {
    this.paymentForm.destroy();
  }

  requestCardNonce = () => {
    this.paymentForm.requestCardNonce();
  }

  render() {
    return (
      <div id="form-container">
        <div id="sq-card-number"></div>
        <div className="third" id="sq-expiration-date"></div>
        <div className="third" id="sq-cvv"></div>
        <div className="third" id="sq-postal-code"></div>
        <button id="sq-creditcard" className="button-credit-card" onClick={this.requestCardNonce}> Pay $1.00</button>
      </div>
    )
  }
}

export default SquarePaymentForm;
