    {
      name: 'Jane Smith',
      photo: '/lady pic.jpeg', // replace with your image link
      testimonial: 'I found what I was looking for!'
    },
    // Add more testimonials as needed
  ];

const Footer = () => {
    return (
        <footer style={{ background: '#c5c2a3', padding: '20px', marginTop: '20px', width: '100%', textAlign: 'center', color: '#272122' }}>
            <p style={{ margin: 0 }}>We can't wait to have you us at the Happiness Summit 2023, where you'll discover the keys to lasting happiness and build a vibrant community of support. See you there!</p>
        </footer>
    );
};

const OptIn = ({ setSubmitted, submitted }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        localStorage.removeItem('formFilled');
    }, []);

    useEffect(() => {
        if(location.pathname === '/thankyou' && !submitted) {
            navigate('/');
        }
    }, [location, navigate, submitted]);

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const data = { firstName, lastName, email };

            const response = await fetch('http://localhost:3000/posts', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(data)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            } else {
                const result = await response.json();
                console.log(result);

                setEmail('');
                setFirstName('');
                setLastName('');
                localStorage.setItem('formFilled', 'true');
                navigate('/thankyou');
            }

        } catch (error) {
            console.error('An error occurred:', error);
        }
    };

    return (
        <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', background: '#b3c5a9', overflowY: 'auto'}}>
            <div style={{width: '100%', overflow: 'hidden'}}>
                <img src="/banner monica.png" alt="Banner" style={{width: '100%', height: 'auto'}}/>
            </div>
            <div style={{width: '100%', padding: '20px', background: 'transparent', textAlign: 'center'}}>
                <p style={{color: '#272122', fontStyle: 'Nourd Bold', fontSize: '20px'}}> Get to know an amazing lineup of renowned experts in the fields of positive psychology, mindfulness, well-being, and personal development. They will share their wisdom, insights, and practical tips to help you find relief, cultivate happiness and lead a more meaningful life.</p>
            </div>
            <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', width: '100%', marginBottom: '20px'}}>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'start', width: '70%', backgroundImage: "url(/form_background.png)", backgroundSize: 'cover', padding: '20px'}}>
    <video style={{width: 'auto', height: '80vh', objectFit: 'contain'}} controls>
        <source src="IMG_6071.mp4" type="video/mp4" />
        Your browser does not support the video tag.
    </video>
    <div style={{background: 'transparent', padding: '20px 20px 20px 0', width: '45%'}}>
        <p style={{color: '#272122', fontStyle: 'Nourd Bold', fontSize: '20px'}}> Are you ready to embark on a journey of self-discovery and unlock the secrets to lasting happiness? Register now for our FREE exclusive virtual summit. </p>
        <form onSubmit={handleSubmit} style={{display: 'flex', flexDirection: 'column', justifyContent: 'space-evenly', alignItems: 'center', width: '100%', height: '50%'}}>
            <input
                type="text"
                name="firstName"
                value={firstName}
                onChange={e => setFirstName(e.target.value)}
                placeholder="First name"
                required
                style={{margin: '10px 0', padding: '10px', width: '90%'}}
            />
            <input
                type="text"
                name="lastName"
                value={lastName}
                onChange={e => setLastName(e.target.value)}
                placeholder="Last name"
                required
                style={{margin: '10px 0', padding: '10px', width: '90%'}}
            />
            <input
                type="email"
                name="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                placeholder="Your email"
                required
                style={{margin: '10px 0', padding: '10px', width: '90%'}}
            />
            <button type="submit" style={{margin: '10px 0', padding: '10px 20px', background: '#D3D3D3', color: 'green', border: '2px solid green', cursor: 'pointer'}}>Claim Your Seat</button>
        </form>
    </div>
    <Carousel
  showArrows={true}
  showThumbs={false}
  showStatus={false}
  showIndicators={false}
  infiniteLoop
  useKeyboardArrows
  autoPlay
  renderArrowPrev={(onClickHandler, hasPrev, label) =>
    hasPrev && (
      <button type="button" onClick={onClickHandler} title={label} style={{ ...styles.arrow, left: 15 }}>
        &#10094;
      </button>
    )
  }
  renderArrowNext={(onClickHandler, hasNext, label) =>
    hasNext && (
      <button type="button" onClick={onClickHandler} title={label} style={{ ...styles.arrow, right: 15 }}>
        &#10095;
      </button>
    )
  }
>
  {testimonials.map((testimonial, index) => (
    <div key={index}>
      <img style={styles.testimonialImage} src={testimonial.photo} alt={testimonial.name} />
      <h3>{testimonial.name}</h3>
      <p>{testimonial.testimonial}</p>
    </div>
  ))}
</Carousel>

</div>

            </div>
            <Footer />
        </div>
    );
};

export default OptIn;
