import { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useSearchParams } from 'react-router-dom';
import OptIn from './components/OptIn';
import ThankYou from './components/ThankYou';

// This is a new component that wraps the OptIn component to handle the "ref" query parameter
const OptInWithRef = (props) => {
  const [searchParams] = useSearchParams();
  const ref = searchParams.get('ref');

  // Pass the "ref" to the OptIn component
  return <OptIn ref={ref} {...props} />;
};

const AppRoutes = () => {
  const [submitted, setSubmitted] = useState(false);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<OptIn setSubmitted={setSubmitted} submitted={submitted} />} />
        <Route path="/optin" element={<OptInWithRef setSubmitted={setSubmitted} submitted={submitted} />} />
        <Route path="/thankyou" element={<ThankYou submitted={submitted} />} />
      </Routes>
    </Router>
  );
};

export default AppRoutes;
