import React, { useState } from 'react';
import axios from 'axios';
//import { Form, Button } from 'react-bootstrap';
import styles from './optIn.Module.css'; // Import styles

const OptIn = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/subscribe', { firstName, lastName, email });
            alert(response.data.message);
            // Clear the form
            setEmail('');
            setFirstName('');
            setLastName('');
        } catch (error) {
            console.error('Something went wrong!', error);
        }
    };

    return (
        <div className={styles.container}>
            <img src="/BANNER.jpg" alt="Banner" style={{width: '100%'}}/>
            <Form onSubmit={handleSubmit} className={styles.form}>
                <Form.Control
                    type="text"
                    name="firstName"
                    value={firstName}
                    onChange={e => setFirstName(e.target.value)}
                    placeholder="First name"
                    required
                    className={styles.input}
                />
                <Form.Control
                    type="text"
                    name="lastName"
                    value={lastName}
                    onChange={e => setLastName(e.target.value)}
                    placeholder="Last name"
                    required
                    className={styles.input}
                />
                <Form.Control
                    type="email"
                    name="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    placeholder="Your email"
                    required
                    className={styles.input}
                />
                <Button type="submit" className={styles.button}>Opt-In</Button>
            </Form>
        </div>
    );
};

export default OptIn;
