import React, { useState } from 'react';
import axios from 'axios';

const OptIn = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/subscribe', { firstName, lastName, email });
            alert(response.data.message);
            // Clear the form
            setEmail('');
            setFirstName('');
            setLastName('');
        } catch (error) {
            console.error('Something went wrong!', error);
        }
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', width: '50vw'}}>
                <img src="/BANNER.jpg" alt="Banner" style={{width: '100%'}}/>
                <form onSubmit={handleSubmit} style={{display: 'flex', flexDirection: 'column', background: 'white', padding: '20px', borderRadius: '8px', marginTop: '20px', width: '100%'}}>
                    <input
                        type="text"
                        name="firstName"
                        value={firstName}
                        onChange={e => setFirstName(e.target.value)}
                        placeholder="First name"
                        required
                        style={{margin: '10px 0', padding: '10px', borderRadius: '4px', width: '100%'}}
                    />
                    <input
                        type="text"
                        name="lastName"
                        value={lastName}
                        onChange={e => setLastName(e.target.value)}
                        placeholder="Last name"
                        required
                        style={{margin: '10px 0', padding: '10px', borderRadius: '4px', width: '100%'}}
                    />
                    <input
                        type="email"
                        name="email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                        placeholder="Your email"
                        required
                        style={{margin: '10px 0', padding: '10px', borderRadius: '4px', width: '100%'}}
                    />
                    <button type="submit" style={{padding: '10px 20px', borderRadius: '4px', background: '#3498db', color: 'white', border: 'none', cursor: 'pointer', marginTop: '10px', width: '100%'}}>Subscribe</button>
                </form>
            </div>
            <div style={{width: '50vw'}}>
                <video controls style={{width: '100%'}}>
                    <source src="/path-to-video.mp4" type="video/mp4"/>
                    Your browser does not support the video tag.
                </video>
            </div>
        </div>
    );
};

export default OptIn;
