import React from 'react';
import { CardNumberElement, CardExpiryElement, CardCvcElement, useStripe, useElements } from '@stripe/react-stripe-js';

const ELEMENT_OPTIONS = {
  style: {
    base: {
      fontSize: '18px',
      color: '#424770',
      '::placeholder': {
        color: '#aab7c4',
      },
    },
    invalid: {
      color: '#9e2146',
    },
  },
};

const StripeCheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    const cardNumberElement = elements.getElement(CardNumberElement);

    const {error, paymentMethod} = await stripe.createPaymentMethod({
      type: 'card',
      card: cardNumberElement,
    });

    if (error) {
      console.log('[error]', error);
    } else {
      console.log('[PaymentMethod]', paymentMethod);
    }
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
  };

  const fieldStyle = {
    width: '100%',
    padding: '10px 14px',
    boxSizing: 'border-box',
    border: '1px solid rgba(0,0,0,0.2)',
    borderRadius: '4px',
    display: 'flex', // Use flex layout for the label
  };

  const inputStyle = {
    width: '100%', // Make the Stripe elements take up all available space
  };

  const paymentBoxStyle = {
    display: 'flex',
    flexDirection: 'column',
    width: '500px',
    padding: '30px',
    borderRadius: '10px',
    boxShadow: '0px 0px 10px 0px rgba(0,0,0,0.1)',
    alignItems: 'center',
    backgroundColor: '#fff',
  };

  return (
    <div style={paymentBoxStyle}>
      <h2>Complete your payment</h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <label style={fieldStyle}>
          Card number
          <div style={inputStyle}>
            <CardNumberElement options={ELEMENT_OPTIONS} />
          </div>
        </label>
        <label style={fieldStyle}>
          Expiration date
          <div style={inputStyle}>
            <CardExpiryElement options={ELEMENT_OPTIONS} />
          </div>
        </label>
        <label style={fieldStyle}>
          CVC
          <div style={inputStyle}>
            <CardCvcElement options={ELEMENT_OPTIONS} />
          </div>
        </label>
        <button type="submit" disabled={!stripe} style={{marginTop: '1rem', fontSize: '1.5rem', padding: '10px 20px', width: '100%', backgroundColor: '#424770', color: '#fff', border: 'none', borderRadius: '4px'}}>Pay</button>
      </form>
    </div>
  );
};

export default StripeCheckoutForm;
