import React, { useState } from 'react';
import axios from 'axios';

const OptIn = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/subscribe', { firstName, lastName, email });
            alert(response.data.message);
            // Clear the form
            setEmail('');
            setFirstName('');
            setLastName('');
        } catch (error) {
            console.error('Something went wrong!', error);
        }
    };

    return (
        <div style={{position: 'relative', textAlign: 'center'}}>
            <img src="/BANNER.jpg" alt="Banner" style={{width: '100%'}}/>
            <form onSubmit={handleSubmit} style={{position: 'absolute', bottom: '20px', left: '20px', background: 'white', padding: '20px', borderRadius: '8px'}}>
                <input
                    type="text"
                    name="firstName"
                    value={firstName}
                    onChange={e => setFirstName(e.target.value)}
                    placeholder="First name"
                    required
                    style={{margin: '10px 0', padding: '10px', borderRadius: '4px'}}
                />
                <input
                    type="text"
                    name="lastName"
                    value={lastName}
                    onChange={e => setLastName(e.target.value)}
                    placeholder="Last name"
                    required
                    style={{margin: '10px 0', padding: '10px', borderRadius: '4px'}}
                />
                <input
                    type="email"
                    name="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    placeholder="Your email"
                    required
                    style={{margin: '10px 0', padding: '10px', borderRadius: '4px'}}
                />
                <button type="submit" style={{padding: '10px 20px', borderRadius: '4px', background: '#3498db', color: 'white', border: 'none', cursor: 'pointer'}}>Subscribe</button>
            </form>
        </div>
    );
};

export default OptIn;
