import React from 'react';
import { CardNumberElement, CardExpiryElement, CardCvcElement, useStripe, useElements } from '@stripe/react-stripe-js';

const ELEMENT_OPTIONS = {
  style: {
    base: {
      fontSize: '18px',
      color: '#424770',
      '::placeholder': {
        color: '#aab7c4',
      },
      padding: '10px 12px',
    },
    invalid: {
      color: '#9e2146',
    },
  },
};

const StripeCheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    const cardNumberElement = elements.getElement(CardNumberElement);

    const {error, paymentMethod} = await stripe.createPaymentMethod({
      type: 'card',
      card: cardNumberElement,
    });

    if (error) {
      console.log('[error]', error);
    } else {
      console.log('[PaymentMethod]', paymentMethod);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', width: '400px', margin: '0 auto', border: '1px solid #000', borderRadius: '10px', padding: '20px' }}>
      <h2 style={{ textAlign: 'center' }}>Complete your payment</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div style={{ border: '1px solid #ccc', borderRadius: '5px', padding: '10px', marginTop: '10px' }}>
          <CardNumberElement options={ELEMENT_OPTIONS} />
        </div>
        <div style={{ border: '1px solid #ccc', borderRadius: '5px', padding: '10px', marginTop: '10px' }}>
          <CardExpiryElement options={ELEMENT_OPTIONS} />
        </div>
        <div style={{ border: '1px solid #ccc', borderRadius: '5px', padding: '10px', marginTop: '10px' }}>
          <CardCvcElement options={ELEMENT_OPTIONS} />
        </div>
        <button type="submit" disabled={!stripe} style={{marginTop: '1rem', fontSize: '1rem', padding: '10px 20px', backgroundColor: '#424770', color: '#fff', border: 'none', borderRadius: '4px'}}>Pay</button>
      </form>
    </div>
  );
};

export default StripeCheckoutForm;
