import { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import OptIn from './components/OptIn';
import ThankYou from './components/ThankYou';

const OptInWithRef = (props) => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const speakerRef = params.get('ref');

  return <OptIn speakerRef={speakerRef} {...props} />;
};

const AppRoutes = () => {
  const [submitted, setSubmitted] = useState(false);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<OptIn setSubmitted={setSubmitted} submitted={submitted} />} />
        <Route path="/" element={<OptInWithRef setSubmitted={setSubmitted} submitted={submitted} />} />
        <Route path="/thankyou" element={<ThankYou submitted={submitted} />} />
      </Routes>
    </Router>
  );
};

export default AppRoutes;
